type User {
id: ID!
name: String!
email: String!
posts: [Post!]!
}
type Query {
user(id: ID!): User
users: [User!]!
}

const resolvers = {
Query: {
user: async (_, { id }, context) => {
return context.db.users.findById(id);
},
users: async (_, __, context) => {
return context.db.users.findAll();
}
},
User: {
posts: async (parent, _, context) => {
return context.db.posts.findByUserId(
parent.id
);
}
}
};

